mapper.select:
select e.emp_name 
	from calendar c join emp e on c.calendar_writer = e.emp_no 
where calendar_no = #{calendarNo}

mapper.update:
update calendar
set 
	calendar_title = #{calendarTitle},
	calendar_content = #{calendarContent},
	calendar_start = #{calendarStart}
	calendar_end = #{calendarEnd}
where calendar_no = #{calendarNo}

update calendar

	calendar_title = #{calendarTitle},
	calendar_content = #{calendarContent},
	calendar_start = #{calendarStart},
	calendar_end = #{calendarEnd}

where calendar_no = #{calendarNo}
