mapper.insert:
insert into chatroom(
	chatroom_no, chatroom_name
)
values(
	#{chatroomNo}, #{chatroomName}
)

mapper.update:
update chatroom 
	set chatroom_name = #{chatroomName}

where chatroom_no = #{chatroomNo}

mapper.select:
select chatroom_no
	from (
	    select chatroom_no
	    from emp_chatroom
	    where emp_no = #{empNo1}
	) A
	where chatroom_no IN (
	    select chatroom_no
	    from emp_chatroom
	    where emp_no = #{empNo2}
	)
	and (
	    select count(*)
	    from emp_chatroom
	    where chatroom_no = A.chatroom_no
	) = 2
