mapper.insert:
insert into document(
	document_no, project_no, document_title, document_content,  
	document_write_time, document_limit_time, document_writer, document_approver,
	 emp_no

)
values (#{documentNo}, #{projectNo}, #{documentTitle}, #{documentContent}, 
		 #{documentWriteTime}, #{documentLimitTime}, #{documentWriter}, #{documentApprover},
		  #{empNo}
		 )

mapper.update:
update document
document_title = #{documentTitle}, document_content = #{documentContent}, document_write_time = #{documentWriteTime}, document_limit_time = #{documentLimitTime}, document_approver = #{documentApprover},
where document_no = #{documentNo}
