mapper.insert:
		insert into project(
			project_no, project_name, project_start_time , project_limit_time, company_no,
			project_writer, emp_no			
		)
values (
    #{projectNo}, #{projectName}, #{projectStartTime}, #{projectLimitTime}, #{companyNo},
    #{projectWriter}, #{empNo}
)

mapper.update:
update project
project_name = #{projectName}, project_start_time = #{projectStartTime}, project_limit_time = #{projectLimitTime},
where project_no = #{projectNo}
